var render=function(){var e={tplPost:'<div class="post" data-id="[[ID]]"><h3>Post: [[TITLE]]</h3><br><h4>Post Date: [[DATE]]</h4><h5>Comments:</h5><div class="comments"></div></div>',tplComment:'<div class="comment" data-id="[[COMMENT_ID]]"><span>ID: [[COMMENT_ID]]</span><br><span>Comment: [[COMMENT]]</span><br><span>User: [[COMMENT_USER]]</span><br><span>Date: [[COMMENT_DATE]]</span><div class="actions"><i class="glyphicon glyphicon-remove comment-remove" data-id="[[COMMENT_ID]]" data-idpost="[[POST_ID]]"></a></div></div>'};return{renderPost:function(a){return e.tplPost.replace("[[ID]]",a.id).replace("[[TITLE]]",a.title).replace("[[DATE]]",a.date)},renderComment:function(a,t){return e.tplComment.replace(/\[\[COMMENT_ID\]\]/g,a.id).replace("[[COMMENT_USER]]",a.user).replace("[[COMMENT]]",a.comment).replace("[[COMMENT_DATE]]",a.date).replace("[[POST_ID]]",t)}}}();